import React, { useEffect } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { listProducts } from '../../actions/productsActions';


function SearchScreen() {
  const dispatch = useDispatch();
  
  // Fetch query parameters from URL
  const searchKeyword = new URLSearchParams(window.location.search).get('keyword');

  // Get product list from Redux store
  const productList = useSelector((state) => state.productsList);
  const { loading, error, products } = productList || {};

  useEffect(() => {
    dispatch(listProducts(searchKeyword || '')); // Fetch products based on search keyword
  }, [dispatch, searchKeyword]);

  return (
    <div>
      {loading ? (
        <h2>Loading...</h2>
      ) : error ? (
        <h2>{error}</h2>
      ) : products.length === 0 ? (
        <h2>No products found</h2>
      ) : (
        <div>
          {products.map((product) => (
            <div key={product._id}>
              <img src={product.image} alt={product.productname} />
              <h3>{product.productname}</h3>
              <p>{product.productinfo}</p>
              <p>Price: ${product.price}</p>
              <p>Rating: {product.rating}</p>
              <p>Stock Count: {product.stockcount}</p>
            </div>
          ))}
        </div>
      )}
    </div>
  );
}

export default SearchScreen;
