import React, { useEffect } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { listProducts } from '../../actions/productsActions';


function SearchScreen() {
  const dispatch = useDispatch();

  // Fetch query parameters from URL
  const searchKeyword = new URLSearchParams(window.location.search).get('keyword');

  // Get product list from Redux store
  const productList = useSelector((state) => state.productsList);
  const { loading, error, products } = productList || {};

  useEffect(() => {
    if (searchKeyword) {
      dispatch(listProducts(searchKeyword)); // Pass the search keyword to the action
    } else {
      dispatch(listProducts()); // Fetch all products if no keyword is provided
    }
  }, [dispatch, searchKeyword]);

  return (
    <div>
      {loading ? (
        <h2>Loading...</h2>
      ) : error ? (
        <h2>{error}</h2>
      ) : products.length === 0 ? (
        <h2>No products found</h2>
      ) : (
        <div>
          {products.map((product) => (
            <div key={product._id}>
              <h3>{product.name}</h3>
              <p>{product.description}</p>
              {/* Add more product details if needed */}
            </div>
          ))}
        </div>
      )}
    </div>
  );
}

export default SearchScreen;
