import {
    USER_LOGIN_REQUEST,
    USER_LOGIN_SUCCESS,
    USER_LOGIN_FAIL,
    USER_SIGNUP_FAIL,
    USER_SIGNUP_REQUEST,
    USER_SIGNUP_SUCCESS,
  } from "../constants/userConstants";
  
  const initialState = {
    loading: false,
    userInfo: null,
    error: null,
  };
  
  // Reducer for user signup
  export const userSignupReducers = (state = initialState, action) => {
    switch (action.type) {
      case USER_SIGNUP_REQUEST:
        return { ...state, loading: true };
      case USER_SIGNUP_SUCCESS:
        return { loading: false, userInfo: action.payload, error: null };
      case USER_SIGNUP_FAIL:
        return { loading: false, userInfo: null, error: action.payload };
      default:
        return state;
    }
  };
  
  // Reducer for user login
  export const userLoginReducers = (state = initialState, action) => {
    switch (action.type) {
      case USER_LOGIN_REQUEST:
        return { ...state, loading: true };
      case USER_LOGIN_SUCCESS:
        return { loading: false, userInfo: action.payload, error: null };
      case USER_LOGIN_FAIL:
        return { loading: false, userInfo: null, error: action.payload };
      default:
        return state;
    }
  };
