import React, { useEffect } from "react";
import { useNavigate } from "react-router-dom";
import { Container, Button } from "react-bootstrap";

function EsewaVerificationScreen() {
  const navigate = useNavigate();

  const initiateEsewaPayment = () => {
    const esewaConfig = {
      amt: "1000", // Amount to be paid
      psc: "0",    // Service Charge
      pdc: "0",    // Delivery Charge
      txAmt: "0",  // Tax Amount
      tAmt: "1000",// Total Amount
      pid: "123456789", // Unique Payment ID
      scd: "YourMerchantCode", // Your Merchant Code
      su: "http://localhost:3000/paymentSuccess", // Success URL
      fu: "http://localhost:3000/paymentFailed",  // Failure URL
    };

    // Construct the URL to redirect to eSewa
    const esewaUrl = `https://esewa.com.np/epay/main?amt=${esewaConfig.amt}&pdc=${esewaConfig.pdc}&psc=${esewaConfig.psc}&txAmt=${esewaConfig.txAmt}&tAmt=${esewaConfig.tAmt}&pid=${esewaConfig.pid}&scd=${esewaConfig.scd}&su=${esewaConfig.su}&fu=${esewaConfig.fu}`;

    // Redirect the user to the eSewa payment page
    window.location.href = esewaUrl;
  };

  useEffect(() => {
    initiateEsewaPayment();
  }, []);

  return (
    <Container className="text-center">
      <h2>Redirecting to eSewa...</h2>
      <Button variant="primary" onClick={initiateEsewaPayment}>
        Click here if not redirected
      </Button>
    </Container>
  );
}

export default EsewaVerificationScreen;
