import React, { useEffect, useState } from "react";
import { Link } from "react-router-dom";
import { Row, Col, ListGroup, Container } from "react-bootstrap";
import Loader from "../Loader";
import Message from "../Message";

function OwnerScreen() {
  const [codDetails, setCodDetails] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState("");

  // Function to fetch COD details from the backend
  const fetchCodDetails = async () => {
    try {
      const response = await fetch("http://localhost:8000/api/cod-details/");
      if (!response.ok) {
        throw new Error(`HTTP error! status: ${response.status}`);
      }
      const data = await response.json();
      setCodDetails(data);
    } catch (error) {
      console.error("Error fetching COD details:", error);
      setError("Failed to fetch COD details.");
    } finally {
      setLoading(false);
    }
  };

  // Use useEffect to call fetchCodDetails when the component mounts
  useEffect(() => {
    fetchCodDetails();
  }, []);

  return (
    <Container>
      <Row>
        <Col md={12}>
          <h1>Ordered Cars and COD Details</h1>
          {loading ? (
            <Loader />
          ) : error ? (
            <Message variant="danger">{error}</Message>
          ) : codDetails.length === 0 ? (
            <Message variant="info">
              No COD orders available. <Link to="/">Go Back</Link>
            </Message>
          ) : (
            <ListGroup variant="flush">
              {codDetails.map((detail) => (
                <ListGroup.Item key={detail.id}>
                  <Row>
                    <Col md={3}>
                      <strong>Name:</strong> {detail.name}
                    </Col>
                    <Col md={3}>
                      <strong>Phone:</strong> {detail.phone}
                    </Col>
                    <Col md={3}>
                      <strong>Email:</strong> {detail.email}
                    </Col>
                    <Col md={3}>
                      <strong>Address:</strong> {detail.address}
                    </Col>
                  </Row>
                </ListGroup.Item>
              ))}
            </ListGroup>
          )}
        </Col>
      </Row>
    </Container>
  );
}

export default OwnerScreen;
