import { createStore, combineReducers, applyMiddleware } from 'redux';
import thunk from 'redux-thunk'; // Fixed import for thunk
import { composeWithDevTools } from 'redux-devtools-extension';
import { productDetailsReducer, productsListReducer } from './reducers/productsReducers';
import { userLoginReducer, userSignupReducer } from './reducers/userReducers'; // Updated to match correct names

const rootReducer = combineReducers({
  productsList: productsListReducer,
  productDetails: productDetailsReducer,
  userLogin: userLoginReducer,
  userSignup: userSignupReducer,
});

const initialState = {};
const middleware = [thunk];

const store = createStore(
  rootReducer,
  initialState,
  composeWithDevTools(applyMiddleware(...middleware))
);

export default store;
