import {
    PRODUCT_LIST_REQUEST,
    PRODUCT_LIST_SUCCESS,
    PRODUCT_LIST_FAIL,
    PRODUCT_DETAILS_REQUEST,
    PRODUCT_DETAILS_SUCCESS,
    PRODUCT_DETAILS_FAIL,
  } from "../constants/productsConstants";

  // Reducer for listing products (also handles search functionality)
  export const productsListReducers = (state = { products: [] }, action) => {
    switch (action.type) {
      case PRODUCT_LIST_REQUEST:
        return { loading: true, products: [] }; // Loading state, empty product list
      case PRODUCT_LIST_SUCCESS:
        return { loading: false, products: action.payload }; // Successfully fetched products
      case PRODUCT_LIST_FAIL:
        return { loading: false, error: action.payload }; // Handle failure case

      default:
        return state; // Return current state for unmatched actions
    }
  };

  // Reducer for product details
  export const productDetailsReducers = (state = { product: {} }, action) => {
    switch (action.type) {
      case PRODUCT_DETAILS_REQUEST:
        return { loading: true, ...state }; // Loading state, preserve current product state
      case PRODUCT_DETAILS_SUCCESS:
        return { loading: false, product: action.payload }; // Successfully fetched product details
      case PRODUCT_DETAILS_FAIL:
        return { loading: false, error: action.payload }; // Handle failure case

      default:
        return state; // Return current state for unmatched actions
    }
  };
