// userReducers.js
import {
    USER_LOGIN_REQUEST,
    USER_LOGIN_SUCCESS,
    USER_LOGIN_FAIL,
    USER_SIGNUP_REQUEST,
    USER_SIGNUP_SUCCESS,
    USER_SIGNUP_FAIL,
  } from "../constants/userConstants";
  
  // Initial state for userSignup
  const initialSignupState = {
    loading: false,
    userInfo: null,
    error: null
  };
  
  // User Signup Reducer
  export const userSignupReducer = (state = initialSignupState, action) => {
    switch (action.type) {
      case USER_SIGNUP_REQUEST:
        return { ...state, loading: true };
      case USER_SIGNUP_SUCCESS:
        return { loading: false, userInfo: action.payload, error: null };
      case USER_SIGNUP_FAIL:
        return { loading: false, userInfo: null, error: action.payload };
      default:
        return state;
    }
  };
  
  // Initial state for userLogin
  const initialLoginState = {
    loading: false,
    userInfo: null,
    error: null
  };
  
  // User Login Reducer
  export const userLoginReducer = (state = initialLoginState, action) => {
    switch (action.type) {
      case USER_LOGIN_REQUEST:
        return { ...state, loading: true };
      case USER_LOGIN_SUCCESS:
        return { loading: false, userInfo: action.payload, error: null };
      case USER_LOGIN_FAIL:
        return { loading: false, userInfo: null, error: action.payload };
      default:
        return state;
    }
  };
